	public static final char NL = '\n';
	
	public static void createIlluminaFile(File illuminaFile) throws IOException {
		try (FileWriter writer = new FileWriter(illuminaFile);){
			// add data
			for (String s : generateIlluminaFileData()) {
				writer.write(s + NL);
			}
		}
	}
	
